let currentPage = 1;
const itemsPerPage = 6;
let filteredAccounts = accounts;

function getVipTag(account) {
    const legendarySkinsCount = account.skins.filter(skin => 
        skin.includes("Legendary") || skin.includes("LEGENDARY")
    ).length;
    const blueEssenceCondition = account.blueEssence >= 60000;
    const isMythic = account.skins.some(skin => skin.includes("Mythic") || skin.includes("MYTHIC"));
    
    if (isMythic) {
        return `<div class="ek-odeme-vip"><span>VIP (+300 TL)</span></div>`;
    }
    
    if (legendarySkinsCount >= 2 || blueEssenceCondition) {
        return `<div class="ek-odeme-vip"><span>VIP (+50 TL)</span></div>`;
    }

    return '';
}


function displayAccounts(page, filteredAccounts = accounts) {
    const start = (page - 1) * itemsPerPage;
    const end = start + itemsPerPage;
    const paginatedAccounts = filteredAccounts.slice(start, end);

    const accountSection = document.getElementById("accounts");
    accountSection.innerHTML = "";

    if (filteredAccounts.length === 0) {
        const noAccountsMessage = document.createElement("div");
        noAccountsMessage.className = "no-accounts-message";
        noAccountsMessage.innerHTML = "Hesap bulunamad谋";
        accountSection.appendChild(noAccountsMessage);
    } else {
        paginatedAccounts.forEach(account => {
            
            const vipTagHtml = getVipTag(account);
            
            const accountBox = document.createElement("div");
            accountBox.className = "account-box";
            accountBox.innerHTML = `
            ${vipTagHtml}
                <h3><span>${account.accNumber}</span><span> Sunucu: ${account.server.toUpperCase()}</span></h3>
                <div class="account-skinBot">
                    <div class="account-skins">Kost眉mler: ${renkliKostumler(account.skins)}</div>
                    <div class="account-upperBot">
                        <div class="account-bottom">
                            Hesap aramla m谋 kas谋ld谋: 
                            <span class="status ${account.isAram ? "yes" : "no"}">
                                ${account.isAram ? 'EVET' : 'HAYIR'}
                            </span>
                        </div>
                        <div class="account-bottom">
                            Son oyun tarihi: ${account.lastGame ? account.lastGame : 'Bilinmiyor'}
                        </div>
                    </div>
                 </div>
                 <div class="essences"> 
                    <div class="account-info">
                        <a id="maviOzImg"><img src="../images/maviOz.png" alt="maviOz image"></a><br>
                        ${account.blueEssence !== undefined ? account.blueEssence : 'Bilinmiyor'}
                    </div>
                    <div class="account-info">
                        <a id="turuncuOzImg"><img src="../images/orangeEss.png" alt="turuncuOz image"></a><br>
                        ${account.orangeEssence !== undefined ? account.orangeEssence : 'Bilinmiyor'}
                    </div>
                </div>
            `;
            accountSection.appendChild(accountBox);
        });
    }

    updatePaginationControls(filteredAccounts);
}




function renkliKostumler(kostumler) {
    return kostumler.map(kostum => {
        const renk = kostum.includes("Epic") || kostum.includes("EPIC") ? "#40f7f7" :
            kostum.includes("Legendary") || kostum.includes("LEGENDARY") ? "#ff3a3a" :
            kostum.includes("Ultimate") || kostum.includes("ULTIMATE") ? "yellow" :
            kostum.includes("Mythic") || kostum.includes("MYTHIC") ? "rgb(192, 42, 230)" :
            "white";
        return `<span class="account-skins" style="color: ${renk}; font-size: 20px;">${kostum}</span>`;
    }).join(', ');
}

function updatePaginationControls(filteredAccounts) {
    const totalPages = Math.ceil(filteredAccounts.length / itemsPerPage);
    const paginationSection = document.getElementById("pagination");
    paginationSection.innerHTML = "";

    const maxVisiblePages = 3;

    const firstPageButton = document.createElement("button");
    firstPageButton.textContent = "<<";
    firstPageButton.onclick = () => {
        currentPage = 1;
        displayAccounts(currentPage, filteredAccounts);
    };
    paginationSection.appendChild(firstPageButton);

    const prevButton = document.createElement("button");
    prevButton.textContent = "<";
    prevButton.onclick = () => {
        if (currentPage > 1) {
            currentPage--;
            displayAccounts(currentPage, filteredAccounts);
        }
    };
    paginationSection.appendChild(prevButton);

    const startPage = Math.max(1, currentPage - Math.floor(maxVisiblePages / 2));
    const endPage = Math.min(totalPages, startPage + maxVisiblePages - 1);

    if (startPage > 1) {
        const firstPage = document.createElement("span");
        firstPage.textContent = "1";
        firstPage.onclick = () => {
            currentPage = 1;
            displayAccounts(currentPage, filteredAccounts);
        };
        paginationSection.appendChild(firstPage);

        if (startPage > 2) {
            const dots = document.createElement("span");
            dots.textContent = "...";
            paginationSection.appendChild(dots);
        }
    }

    for (let i = startPage; i <= endPage; i++) {
        const pageButton = document.createElement("span");
        pageButton.textContent = i;
        pageButton.className = currentPage === i ? "active" : "";
        pageButton.onclick = () => {
            currentPage = i;
            displayAccounts(currentPage, filteredAccounts);
        };
        paginationSection.appendChild(pageButton);
    }

    if (endPage < totalPages) {
        if (endPage < totalPages - 1) {
            const dots = document.createElement("span");
            dots.textContent = "...";
            paginationSection.appendChild(dots);
        }

        const lastPage = document.createElement("span");
        lastPage.textContent = totalPages;
        lastPage.onclick = () => {
            currentPage = totalPages;
            displayAccounts(currentPage, filteredAccounts);
        };
        paginationSection.appendChild(lastPage);
    }

    const nextButton = document.createElement("button");
    nextButton.textContent = ">";
    nextButton.onclick = () => {
        if (currentPage < totalPages) {
            currentPage++;
            displayAccounts(currentPage, filteredAccounts);
        }
    };
    paginationSection.appendChild(nextButton);

    const lastPageButton = document.createElement("button");
    lastPageButton.textContent = ">>";
    lastPageButton.onclick = () => {
        currentPage = totalPages;
        displayAccounts(currentPage, filteredAccounts);
    };
    paginationSection.appendChild(lastPageButton);
}


document.addEventListener("DOMContentLoaded", () => {
    displayAccounts(currentPage);
    document.getElementById("filterSubmit").addEventListener("click", filterAccounts);
    document.getElementById("resetFilters").addEventListener("click", resetFilters);
});


function normalizeServerName(server) {
    if (server.startsWith("tr")) {
        return "tr";
    } else if (server.startsWith("euw")) {
        return "euw";
    }
      else if (server.startsWith("eune")) {
        return "eune";
  } 
      else if (server.startsWith("na")) {
        return "na";
}

    return server;
}

function filterAccounts() {
    const serverFilter = document.getElementById("server").value.toLowerCase();
    const blueEssenceFilter = document.getElementById("maviOz").value;
    const skinsFilter = document.getElementById("skins").value.toLowerCase();
    const accNumberFilter = document.getElementById("accNumberFilter").value.trim();

    filteredAccounts = accounts.filter(account => {
        const normalizedServer = normalizeServerName(account.server.toLowerCase());
        const serverMatch = serverFilter === "all" || normalizedServer === serverFilter;
        const blueEssenceMatch = blueEssenceFilter === "all" ||
            (blueEssenceFilter === "30.000+" && account.blueEssence >= 30000) ||
            (blueEssenceFilter === "40.000+" && account.blueEssence >= 40000) ||
            (blueEssenceFilter === "50.000+" && account.blueEssence >= 50000) ||
            (blueEssenceFilter === "60.000+" && account.blueEssence >= 60000);
        const skinsMatch = account.skins.some(skin => skin.toLowerCase().includes(skinsFilter));
        const accNumberMatch = accNumberFilter === "" || account.accNumber.includes(accNumberFilter);

        return serverMatch && blueEssenceMatch && skinsMatch && accNumberMatch;
    });

    displayAccounts(1, filteredAccounts);
}


function resetFilters() {
    document.getElementById("server").value = "all";
    document.getElementById("maviOz").value = "all";
    document.getElementById("skins").value = "";
    document.getElementById("accNumberFilter").value = "";
    filteredAccounts = accounts;
    displayAccounts(currentPage, filteredAccounts);
}


